import Foundation

extension NSPredicate {
    
    convenience init(key: String, id: Int64) {
        
        let predicateString = String(format: "%@ == %li", key, id)
        self.init(format: predicateString)
    }
    
    convenience init(id: Int64) {
        
        let predicateString = String(format: "id == %li", id)
        self.init(format: predicateString)
    }
}
